# Tic-Tac-Toe Game

# Function to print the board
def print_board(board):
    for i in range(3):
        print(" | ".join(board[i]))
        if i < 2:
            print("---------")

# Function to check if any player has won
def check_winner(board, player):
    # Check rows
    for i in range(3):
        if board[i][0] == board[i][1] == board[i][2] == player:
            return True
    # Check columns
    for i in range(3):
        if board[0][i] == board[1][i] == board[2][i] == player:
            return True
    # Check diagonals
    if board[0][0] == board[1][1] == board[2][2] == player:
        return True
    if board[0][2] == board[1][1] == board[2][0] == player:
        return True
    return False

# Function to get a human player's move
def get_human_move(board):
    while True:
        move = input("Enter your move (row, column): ")
        row, col = move.split(",")
        row = int(row.strip()) - 1
        col = int(col.strip()) - 1
        if 0 <= row < 3 and 0 <= col < 3 and board[row][col] == " ":
            return row, col
        print("Invalid move, try again!")

# Function to get a computer player's move using simple AI
def get_computer_move(board):
    # Check if the computer can win in the next move
    for i in range(3):
        for j in range(3):
            if board[i][j] == " ":
                board[i][j] = "O"
                if check_winner(board, "O"):
                    return i, j
                board[i][j] = " "

    # Check if the human can win in the next move
    for i in range(3):
        for j in range(3):
            if board[i][j] == " ":
                board[i][j] = "X"
                if check_winner(board, "X"):
                    return i, j
                board[i][j] = " "

    # Try to take one of the corners
    corners = [(0, 0), (0, 2), (2, 0), (2, 2)]
    for corner in corners:
        if board[corner[0]][corner[1]] == " ":
            return corner

    # Take the center if available
    if board[1][1] == " ":
        return 1, 1

    # Take any available edge
    edges = [(0, 1), (1, 0), (1, 2), (2, 1)]
    for edge in edges:
        if board[edge[0]][edge[1]] == " ":
            return edge

# Main function to run the game
def play_game():
    # Create an empty board
    board = [[" " for _ in range(3)] for _ in range(3)]

    # Player1 is the human, Player2 is the computer
    players = ["X", "O"]

    # Alternate turns until the game is over
    for turn in range(9):
        print_board(board)
        print("Player", players[turn % 2], "'s turn.")

        if turn % 2 == 0:
            row, col = get_human_move(board)
        else:
            row, col = get_computer_move(board)

        board[row][col] = players[turn % 2]

        if check_winner(board, players[turn % 2]):
            print_board(board)
            print("Player", players[turn % 2], "wins!")
            return

    # If all 9 turns completed and no winner, it's a tie
    print_board(board)
    print("It's a tie!")

# Play the game
play_game()
